# define ER_aiowrite		3
# define ER_alloc	 	4
# define ER_badadar   		5
# define ER_bind	 	6
# define ER_brk		 	7
# define ER_chdir	 	8
# define ER_close	 	9
# define ER_connect	 	10
# define ER_create	 	11
# define ER_delete	 	12
# define ER_fcntl	 	13
# define ER_flush	 	14
# define ER_fopen	 	15
# define ER_force	 	16
# define ER_fread	 	17
# define ER_fseek		18
# define ER_fstat		19
# define ER_fsync		20
# define ER_gethostbyname 	21
# define ER_getsockname 	22
# define ER_inet_addr 		23
# define ER_ioctl 		24
# define ER_link		25
# define ER_lio_listio		26
# define ER_list		27
# define ER_listen		28
# define ER_lseek		29
# define ER_mkdir		30
# define ER_mlock		31
# define ER_mmap		32
# define ER_munmap		33
# define ER_netbios		34
# define ER_open		35
# define ER_opendir		36
# define ER_popen		37
# define ER_read		38
# define ER_rename		39
# define ER_revent		40
# define ER_rmdir		41
# define ER_sbrk		42
# define ER_sense		43
# define ER_setuid		44
# define ER_sevent		45
# define ER_shmat		46
# define ER_shmctl		47
# define ER_shmdt		48
# define ER_shmget		49
# define ER_socket		50
# define ER_stat		51
# define ER_system		52
# define ER_t_accept  		53
# define ER_t_alloc   		54
# define ER_t_connect   	55
